  outline: 1px solid var(--text-default);
}

.header-logo {
  display: block;
  flex-shrink: 0;
  font-weight: bold;
  padding: 0.4rem 0;
}

.header-logo img {
  height: 3.2rem;
  width: auto;
}

.header-links {
  display: flex;
  align-items: center;
  gap: 0.8rem;
}

.header-link {
  padding: 0.8rem 1.2rem;
  border-radius: 4px;
}

.header-link:hover {
  text-decoration: underline;
}

.header-component {
  color: var(--header-text-color);
}

.header-title {
  width: 100%;
  padding: 2.4rem;
  min-height: 13.6rem;
}

.header-h1 {
  color: var(--header-text-color);
  text-align: center;
  margin-bottom: 2.4rem;
  font-size: var(--font-size-h0-mobile);
  line-height: 1;
  font-family: var(--heading-font-family);
  font-weight: bold;
}

.header-search {
  margin: auto;
  display: flex;
  width: 100%;
  padding: 16px;
  align-items: flex-start;
  gap: 12px;
  border-radius: 8px;
  background-image: url(https://ic.ortto.app/l/magic-ffffff-23-23.png);
  background-position: 20px;
  background-repeat: no-repeat;
  text-indent: 40px;
}

.header-search::placeholder {
  color: var(--header-text-color);
  font-family: var(--body-font);
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 24px;
}

color-mode-toggle {
  display: flex;
  align-items: center;
}

.input-wrapper {
  position: relative;
}

.kb-search-widget {
  margin-left: auto;
  margin-right: auto;
  max-width: 560px;
  display: flex;
  width: 100%;
  border: none;
  outline: none;
  font: inherit;
  padding: 16px;
  color: var(--header-text-color);
  background: var(--background-search-input);
  box-shadow: var(--shadow);
  text-indent: 36px;
  border-radius: 8px;
}

.kb-search-widget::placeholder {
  color: var(--header-text-color);
  opacity: 1;
}

.icon--input {
  position: absolute;
  top: 16px;
  left: 16px;
